package pl.mati.uonet.backend

import io.github.wulkanowy.sdk.Sdk
import okhttp3.logging.HttpLoggingInterceptor
import java.io.File

//private const val API_KEY = "012345678901234567890123456789AB"
private const val CERT_KEY = "7EBA57E1DDBA1C249D097A9FF1C9CCDD45351A6A"
private const val PRIVATE_KEY = ""

fun getSdkObject(): Sdk {

    if (System.getenv("MODE") == "DEV") {
        return Sdk().apply {
            privateKey = PRIVATE_KEY
            certKey = CERT_KEY

            mobileBaseUrl = "https://api.fakelog.cf/powiatwulkanowy"
            mode = Sdk.Mode.API
            symbol = "powiatwulkanowy"
            logLevel = HttpLoggingInterceptor.Level.NONE

            schoolSymbol = "123456"
            studentId = 15
            classId = 14
        }
    } else {
        return Sdk().apply {

            File("secrets.txt").bufferedReader().use {
                email = it.readLine()
                password = it.readLine()
                privateKey = it.readLine()
                certKey = it.readLine()
            }

            mobileBaseUrl = "https://uonetplus-komunikacja.edu.lublin.eu/lublin"
            scrapperBaseUrl = "https://edu.lublin.eu"
            mode = Sdk.Mode.API
            symbol = "lublin"
            loginType = Sdk.ScrapperLoginType.ADFSLightCufs
            buildTag = "Fortress API"
            logLevel = HttpLoggingInterceptor.Level.NONE

            schoolSymbol = "009625"
            studentId = 188412
            // classId = 7836
            diaryId = 25038
        }
    }
}

